async def start(update: Update, context: ContextTypes.DEFAULT_TYPE):
    user_id = update.effective_user.id
    user_state[user_id] = {"scene": 1, "score": 0.0, "glitch": False}
    await update.effective_chat.send_message("üëª –¢—ã –ø—Ä–æ–≤–µ–ª–∞ –º–µ—Ä–æ–ø—Ä–∏—è—Ç–∏–µ, –Ω–∞ –∫–æ—Ç–æ—Ä–æ–µ —Å–Ω–æ–≤–∞ –Ω–∏–∫—Ç–æ –Ω–µ –ø—Ä–∏—à—ë–ª. –£—Å—Ç–∞–≤ –∫–∞–∫ —Å–∫–æ—Ç–∏–Ω–∞, —Ç—ã –ª–æ–∂–∏—à—å—Å—è –∏ —Å—Ä–∞–∑—É –≤—ã—Ä—É–±–∞–µ—à—å—Å—è. –ü—Ä–æ—Å—ã–ø–∞–µ—à—å—Å—è —Ç—ã, –ø–æ—á–µ–º—É-—Ç–æ, –≤—Å—ë –≤ —Ç–æ—Ç –∂–µ –¥–µ–Ω—å –º–µ—Ä–æ–ø—Ä–∏—è—Ç–∏—è. –ß—Ç–æ —Ç—ã —Å–µ–≥–æ–¥–Ω—è –∏–∑–º–µ–Ω–∏—à—å?")
    await send_scene(update, user_id)

async def send_scene(update: Update, user_id):
    state = user_state[user_id]
    scene_id = state["scene"]

    if scene_id > len(SCENES):
        if state["score"] >= 4.0:
            await update.effective_chat.send_message("üéâ –¢—ã –≤—Å—ë –∏–∑–º–µ–Ω–∏–ª–∞. –õ—é–¥–∏ –ø—Ä–∏—à–ª–∏. –ö—Ä—É–≥–æ–≤–æ—Ä–æ—Ç –≤—Ä–µ–º–µ–Ω–∏ –≤–æ–∑–æ–±–Ω–∞–≤–ª—è–µ—Ç—Å—è. –ù–æ —Ç–æ—á–Ω–æ –ª–∏ —Ç—ã - —Ö–æ–∑—è–π–∫–∞ —Å–≤–æ–µ–≥–æ —Å–æ–∑–Ω–∞–Ω–∏—è?")
        else:
            await update.effective_chat.send_message("ü©∏ –¢—ã —Å–Ω–æ–≤–∞ –ø—Ä–æ—Å—ã–ø–∞–µ—à—å—Å—è. –ù–æ —É–∂–µ –Ω–µ –æ–¥–Ω–∞. [‚ñí‚ñí‚ñí] –≤–Ω—É—Ç—Ä–∏. /start")
        return

    scene = SCENES[scene_id]

    if scene_id > GLITCH_ACTIVE_AFTER:
        state["glitch"] = True

    await update.effective_chat.send_message(scene["text"])

    # –û—Ç–ø—Ä–∞–≤–ª—è–µ–º –≤–∞—Ä–∏–∞–Ω—Ç—ã –≤ —Å–æ–æ–±—â–µ–Ω–∏–∏, —á—Ç–æ–±—ã –Ω–µ –æ–±—Ä–µ–∑–∞–ª–∏—Å—å
    full_choices = "\n".join(scene["choices"])
    await update.effective_chat.send_message(full_choices)

    # –ö–æ—Ä–æ—Ç–∫–∏–µ –∫–Ω–æ–ø–∫–∏
    if state.get("glitch") and not scene.get("glitch"):
        reply_markup = ReplyKeyboardMarkup([["‚ñí"], ["‚ñí"], ["‚ñí"]], one_time_keyboard=True, resize_keyboard=True)
    else:
        reply_markup = ReplyKeyboardMarkup([["–ê"], ["–ë"], ["–í"]], one_time_keyboard=True, resize_keyboard=True)

    await update.effective_chat.send_message("–í—ã–±–µ—Ä–∏ –≤–∞—Ä–∏–∞–Ω—Ç:", reply_markup=reply_markup)

async def handle_choice(update: Update, context: ContextTypes.DEFAULT_TYPE):
    user_id = update.effective_user.id
    if user_id not in user_state:
        await update.effective_chat.send_message("–ù–∞—á–Ω–∏ —Å –∫–æ–º–∞–Ω–¥—ã /start.")
        return

    state = user_state[user_id]
    scene_id = state["scene"]
    scene = SCENES.get(scene_id)
    if not scene:
        await update.effective_chat.send_message("–°—Ü–µ–Ω–∞ –Ω–µ –Ω–∞–π–¥–µ–Ω–∞. /start")
        return

    text = update.message.text.strip().upper()
    letter_to_index = {"–ê": 0, "–ë": 1, "–í": 2}

    if state.get("glitch") and not scene.get("glitch"):
        index = random.randint(0, 2)
        await update.effective_chat.send_message("[‚ñí‚ñí‚ñí] –Ø –≤—ã–±—Ä–∞–ª –∑–∞ —Ç–µ–±—è.")
    elif text not in letter_to_index:
        await update.effective_chat.send_message("‚ùó –ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –Ω–∞–∂–º–∏ –æ–¥–Ω—É –∏–∑ –∫–Ω–æ–ø–æ–∫: –ê, –ë –∏–ª–∏ –í.")
        return
    else:
        index = letter_to_index[text]

    await update.effective_chat.send_message(scene["outcomes"][index])

    if not scene.get("glitch"):
        state["score"] += scene["scores"][index]

    state["scene"] += 1
    await send_scene(update, user_id)

